'use strict';

jQuery.request = function () {

	var href = arguments[0];
	var csrf = arguments[1];
	var type_href = ({}).toString.call(href).match(/\s([a-z|A-Z]+)/)[1].toLowerCase();
	var type_csrf = ({}).toString.call(csrf).match(/\s([a-z|A-Z]+)/)[1].toLowerCase();

	if (type_href == 'string' && type_csrf == 'string') {
		$('body').append('<form method="post" action="' + href + '" class="hidden" id="request"><input type="hidden" name="' + csrf + '"></form>');
		$('#request').trigger('submit');
	}

};